import { Component } from '@angular/core';
import { TaskService } from 'src/app/services/task.service';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.scss']
})
export class TaskComponent {
   tasks: any[] = [
    {
       title:'1',
       description:"2xhh"

    },
    {
       title:'1',
       description:"2xhh"

    },
    {
       title:'1',
       description:"2xhh",
       status:'all'

    }
   ];
  filteredTasks: any[] = [];
  selectedTask: any = null;
  filterStatus: string = 'all';

  taskForm = {
    title: '',
    description: ''
  };

  constructor(private taskService: TaskService) {}

  ngOnInit(): void {
  this.filteredTasks = this.tasks;

    this.getTasks();
  }

  getTasks() {
    // this.taskService.getTasks().subscribe((res: any) => {
    //   this.tasks = res;
    //   this.filteredTasks = this.tasks;
    // });
  }

  addTask() {
    // if (!this.taskForm.title) return alert('Task title is required');
    // this.taskService.addTask(this.taskForm).subscribe(() => {
    //   this.taskForm = { title: '', description: '' };
    //   this.getTasks();
    // });
  }

  editTask(task: any) {
    this.selectedTask = { ...task };
  }

  updateTask() {
    // this.taskService.updateTask(this.selectedTask._id, this.selectedTask).subscribe(() => {
    //   this.selectedTask = null;
    //   this.getTasks();
    // });
  }

  deleteTask(id: string) {
    // if (confirm('Are you sure to delete this task?')) {
    //   this.taskService.deleteTask(id).subscribe(() => this.getTasks());
    // }
  }

  markCompleted(task: any) {
    // task.status = 'completed';
    // this.taskService.updateTask(task._id, task).subscribe(() => this.getTasks());
  }

  filterTasks(status: string) {
    this.filterStatus = status;
    if (status === 'all') {
      this.filteredTasks = this.tasks;
    } else {
      this.filteredTasks = this.tasks.filter(t => t.status === status);
    }
  }


}
<div class="task-container">
  <h2>My Tasks</h2>

  <!-- Add Task Form -->
  <div class="task-form">
    <input type="text" placeholder="Task Title" [(ngModel)]="taskForm.title">
    <input type="text" placeholder="Task Description" [(ngModel)]="taskForm.description">
    <button (click)="addTask()">Add Task</button>
  </div>

  <!-- Filter Buttons -->
  <div class="filters">
    <button (click)="filterTasks('all')" [class.active]="filterStatus === 'all'">All</button>
    <button (click)="filterTasks('pending')" [class.active]="filterStatus === 'pending'">Pending</button>
    <button (click)="filterTasks('completed')" [class.active]="filterStatus === 'completed'">Completed</button>
  </div>

  <!-- Task List -->
  <div *ngFor="let task of filteredTasks" class="task-item">
    <div *ngIf="selectedTask?._id !== task._id">
      <h3 [class.completed]="task.status === 'completed'">{{ task.title }}</h3>
      <p>{{ task.description }}</p>
      <button (click)="editTask(task)">Edit</button>
      <button (click)="deleteTask(task._id)">Delete</button>
      <button *ngIf="task.status !== 'completed'" (click)="markCompleted(task)">Mark Done</button>
    </div>

    <!-- Edit Mode -->
    <div *ngIf="selectedTask?._id === task._id">
      <input [(ngModel)]="selectedTask.title">
      <input [(ngModel)]="selectedTask.description">
      <button (click)="updateTask()">Update</button>
      <button (click)="selectedTask = null">Cancel</button>
    </div>
  </div>
</div>
