 addTask(taskForm:any) {
  return this.http.post(`${this.serverUrl}/addtask`,taskForm);
  }

  getTasks() {
    const userId=localStorage.getItem('userId')
  return this.http.get(`${this.serverUrl}/getTask/${userId}`,);
  }
  updateTask(taskId:any,updateTask:any)
  {
     return this.http.post(`${this.serverUrl}/updateTask/${taskId}`,updateTask); 
