import { Component } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {

    user = {
    username: '',
    email: '',
    password: ''
  };
  constructor(private authService:AuthService)
  {

  }

    register() {
    if (!this.user.username || !this.user.email || !this.user.password) {
      alert('All fields are required');
      return;
    }

    this.authService.register(this.user).subscribe({
      next: (res) => {
        alert('Registration successful!');
        console.log(res);
      },
      error: (err) => {
        alert('Registration failed. Try again!');
        console.error(err);
      }
    });
  }

}
<div class="register-container">
  <form class="register-form" (ngSubmit)="register()">
    <h2>Create Account</h2>

    <div class="form-group">
      <label>Username</label>
      <input type="text" [(ngModel)]="user.username" name="username" placeholder="Enter username" required />
    </div>

    <div class="form-group">
      <label>Email</label>
      <input type="email" [(ngModel)]="user.email" name="email" placeholder="Enter email" required />
    </div>

    <div class="form-group">
      <label>Password</label>
      <input type="password" [(ngModel)]="user.password" name="password" placeholder="Enter password" required />
    </div>

    <button type="submit">Register</button>

    <p class="login-text">
      Already have an account? <a routerLink="/login">Login here</a>
    </p>
  </form>
</div>
